        return new Source("{% extends 'base.html.twig' %}

{% block title %}Les produits
{% endblock %}

{% block body %}
    <div class=\"bg-light p-3\">
        <h1>Les produits</h1>
    </div>

    <div class=\"row\">
        {% for product in products %}
            <div class=\"col-4 mt-4\">
                <div class=\"border border-dark rounded p-2 clearfix\">
                    <h2>
                        {{ product.title }}
                        -
                        {{ product.price }}
                        €
                    </h2>
                    <img class=\"img-fluid rounded\" src=\"images/produit.jpg\" alt=\"Image de {{ product.title }}\">
                    <hr>
                    <a href=\"{{ path(\"panier_add\" ,{id:product.id}) }}\" class=\"btn btn-success float-right\">
                        <i class=\"fas fa-shopping-cart mr-1\"></i>
                        Ajouter
                    </a>
                </div>

            </div>
        {% endfor %}
    </div>
{% endblock %}
", "product/index.html.twig", "/home/sanadi/app/panier/templates/product/index.html.twig");
